mapper.insert:
INSERT INTO category(
	category_name,
	member_id
) VALUES(
	#{categoryName},
	#{memberId}
)

mapper.select:
SELECT COUNT(category_name)
FROM category
WHERE member_id   = #{memberId} 
AND category_name = #{categoryName}

mapper.delete:
DELETE
FROM category
WHERE category_name = #{categoryName}
AND member_id = #{memberId}
